UPDATE quran_text SET quran_text.simple = 'يا أيها الناس اتقوا ربكم الذي خلقكم من نفس واحدة وخلق منها زوجها وبث منهما رجالا كثيرا ونساء واتقوا الله الذي تساءلون به والأرحام إن الله كان عليكم رقيبا' WHERE quran_text.index = 494 AND quran_text.sura = 4 AND quran_text.aya = 1 LIMIT 1;
UPDATE quran_text SET quran_text.simple = 'يا أيها الذين آمنوا أوفوا بالعقود أحلت لكم بهيمة الأنعام إلا ما يتلى عليكم غير محلي الصيد وأنتم حرم إن الله يحكم ما يريد' WHERE quran_text.index = 670 AND quran_text.sura = 5 AND quran_text.aya = 1 LIMIT 1;
UPDATE quran_text SET quran_text.simple = 'الحمد لله الذي خلق السماوات والأرض وجعل الظلمات والنور ثم الذين كفروا بربهم يعدلون' WHERE quran_text.index = 790 AND quran_text.sura = 6 AND quran_text.aya = 1 LIMIT 1;
